mapper.update:
update vip_detail
mobile=#{e.mobile}, name=#{e.name}, sex=#{e.sex}, birth_day=#{e.birthDay}, qq=#{e.qq}, email=#{e.email}, address=#{e.address}, zip_code=#{e.zipCode}, rank_id=#{e.rank.id}, total_discount_money=#{e.totalDiscountMoney}, total_comsume_money=#{e.totalConsumeMoney}, remark=#{e.remark}, last_consume_time=#{e.lastConsumeTime},
where id=#{id}

mapper.select:
select * from vip_detail where 1=1

	and (account like #{key}
	or name like #{key}
	or mobile like #{key})

order by account
